export function initDataProvider(optionsDataProvider) {
    let dataProvider;
    if (typeof optionsDataProvider === 'function') {
        dataProvider = optionsDataProvider();
    }
    else
        dataProvider = Promise.resolve(optionsDataProvider);
    dataProvider = dataProvider.then(async (dp) => {
        if (dp)
            return dp;
        return new (await import('./JsonEntityFileStorage.js')).JsonFileDataProvider('./db');
    });
    return dataProvider;
}
